import { useState } from "react"

export default function App() {

    const [numero, setNumero] = useState(0)

    function aumentar() {
        setNumero(numero++)
    }

    function diminuir() {
        setNumero(numero--)
    }

  
   

    return <section>
        <h1>Contador</h1>
        <p>{numero}</p>
        <button onClick={diminuir}>menos</button>
        <button onClick={resetar}>zerar</button>
        <button onClick={aumentar}>mais</button>
        
    </section>
}
